import { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";

import vesikalık from "../../assets/images/vesikalık.jpg";
import { ArrowRightCircle } from 'react-bootstrap-icons';
import 'animate.css';
import "./banner.css";
import TrackVisibility from 'react-on-screen';

export const Banner = () => {
    const [loopNum, setLoopNum] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(300 - Math.random() * 100);
    const [index, setIndex] = useState(1);
    const toRotate = ["Sofware Developer", "Front End Developer"];
    const period = 500;

    useEffect(() => {
        let ticker = setInterval(() => {
            tick();
        }, delta);

        return () => { clearInterval(ticker) };
    }, [text])

    const tick = () => {
        let i = loopNum % toRotate.length;
        let fullText = toRotate[i];
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1);

        setText(updatedText);

        if (isDeleting) {
            setDelta(prevDelta => prevDelta / 2);
        }

        if (!isDeleting && updatedText === fullText) {
            setIsDeleting(true);
            setIndex(prevIndex => prevIndex - 1);
            setDelta(period);
        } else if (isDeleting && updatedText === '') {
            setIsDeleting(false);
            setLoopNum(loopNum + 1);
            setIndex(1);
            setDelta(500);
        } else {
            setIndex(prevIndex => prevIndex + 1);
        }
    }

    return (
        <section className="banner" id="home">
    <Row style={{ display: "flex", flex: 1 }}>
        {/* Metinler bölümü */}
        <Col xs={12} md={6} xl={7}>
            <TrackVisibility>
                {({ isVisible }) =>
                    <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                        <span className="tagline">Welcome to my Portfolio</span>
                        <h1>{`Hi! I'm Gizem`} <span className="txt-rotate" dataPeriod="1000" data-rotate='[ "Web Developer", "Web Designer", "UI/UX Designer" ]'><span className="wrap">{text}</span></span></h1>
                        <p>I am a 3rd-year Computer Engineering student at Trakya
                            University. By prioritizing my studies, I managed to achieve the top
                            position in my class with my grades from the 1st and 2nd years. I
                            have learned programming languages such as Python, C, 8086
                            Assembly, Java, Flutter, and React.Throughout my academic journey, I have honed my
                            problem-solving and analytical skills, maintaining an open attitude
                            towards learning and keeping myself updated with industry
                            innovations. As an energetic and ambitious aspiring computer
                            engineer, I am eager to gain experience in the software world.</p>
                        <button onClick={() => console.log('connect')}>Let’s Connect <ArrowRightCircle size={25} /></button>
                    </div>}
            </TrackVisibility>
        </Col>
        {/* Resim bölümü */}
        <div >
  <div class="content">
    <img src={vesikalık} alt="Header Img"/>
  </div>
</div>
    </Row>
</section>

    )
}
