import { Button, Carousel, Modal, ConfigProvider } from 'antd';
import React, { useState } from 'react';
import Video from "../../assets/videos/demo.mp4";
const AppHero = (props) => {
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <ConfigProvider
      theme={{
        components: {
          Button: {
            defaultHoverColor: '#d11ba7',
            defaultHoverBorderColor: '#d11ba7',
          }

        },

      }}
    >
      <div id="hero" className="heroBlock">
        <Carousel>
          {props.itemsHeroList.map(item => (

            <div key={item.key} className="container-fluid">
              <div className='content'>
                <h3>{item.title}</h3>
                <p>{item.content}</p>
                <div className="btnHolder">
                  <Button type="primary" size="large" href={item.link} target="_blank" rel="noopener noreferrer" style={{ backgroundColor: "#d11ba7" }}>Github Repository</Button>

                  <Button
                    size="large"
                    onClick={showModal}
                    className="watch-demo-button"
                  >
                    <i className="fas fa-desktop"></i> Watch a Demo
                  </Button>
                </div>


              </div>
            </div>


          ))}
        </Carousel>
        <Modal
          width="800"
          height="800"
          title="Basic Modal"
          visible={isModalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
          cancelText="Close"
          closable={false}
          okButtonProps={{ style: { display: 'none' } }}
          cancelButtonProps={{ type: 'primary' }}
        >
          <iframe title='video' width="800" height="800" src={Video}></iframe>
        </Modal>
      </div>
    </ConfigProvider>
  );
}

export default AppHero;
