import React from "react";
import { Row, Col } from 'antd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChartPie, faDesktop, faDatabase } from '@fortawesome/free-solid-svg-icons';

const items = [
    {
        key: '1',
        title: 'Trakya Üniversitesi Bilgisayar Mühendisliği 3. sınıf öğrencisiyim.',
        content: 'Derslerime gereken önemi göstererek 1. ve 2. sınıftaki ortalamalarımla sınıf birinciliğini elde ettim.',
        icon: <FontAwesomeIcon icon={faChartPie} />,
    },
    {
        key: '2',
        title: 'Python, C, 8086 Assembly, Java, Flutter ve Dart programlama dillerini öğrendim.',
        content: 'Flutter kullanarak 2 adet proje geliştirdim.',
        icon: <FontAwesomeIcon icon={faDesktop} />,
    },
    {
        key: '3',
        title: "Projelerim",
        content: 'İlk Projem Trakya Üniversitesindeki öğrencilerin kullanabileceği bir haberleşme uygulamasıydı ve sınıf arkadaşlarımla birlikte geliştirdik.Diğer projem ise bireysel olarak geliştirdiğim, “Design Patterns” hakkında bilgi veren bir mobil uygulama oldu.',
        icon: <FontAwesomeIcon icon={faDatabase} />,
    },
];

function AppAbout() {
    return (
        <div className="block aboutBlock">
            <div className="container-fluid">
                <div className="titleHolder">
                    <h2>About Me</h2>
                    <p>YAptıklarım projelerim kişisel bilgilerim</p>
                </div>
                <div className="contentHolder">
                    <p>Trakya Üniversitesi Bilgisayar Mühendisliği 3. sınıf öğrencisiyim.
                        Derslerime gereken önemi göstererek 1. ve 2. sınıftaki
                        ortalamalarımla sınıf birinciliğini elde ettim. Python, C, 8086
                        Assembly, Java, Flutter ve Dart programlama dillerini öğrendim.
                        Flutter kullanarak 2 adet proje geliştirdim.</p>
                </div>
                <Row gutter={[16, 16]}>
                    {items.map(item => {
                        return (
                            <Col span={8} key={item.key}>
                                <div className="content">
                                    <div className="icon">
                                        {item.icon}
                                    </div>
                                    <h3>{item.title}</h3>
                                    <p>{item.content}</p>
                                </div>
                            </Col>
                        );
                    })}
                </Row>
            </div>
        </div>
    );
}

export default AppAbout;
