import { Row, Col } from "antd";
import React from "react";
import { Card } from 'antd';
import Image1 from "../../assets/images/C.png"
import Image2 from "../../assets/images/react.png"
import Image3 from "../../assets/images/flutterjpg.jpg"
import Image4 from "../../assets/images/python.svg"
import Image5 from "../../assets/images/java.svg"

const { Meta } = Card;

function AppFeature() {
    // Resimler ve açıklamaları bir liste içinde tanımla
    const features = [
        { image: Image1, title: "C", description: "C programlama dili" },
        { image: Image2, title: "React", description: "React framework" },
        { image: Image3, title: "Flutter", description: "Flutter framework" },
        { image: Image4, title: "Python", description: "Python programlama dili" },
        { image: Image5, title: "Java", description: "Java programlama dili" }
    ];

    return (
        <div className="block featureBlock bgGray">
            <div className="container-fluid">
                <Row gutter={[16, 16]}>
                    {/* features listesini map fonksiyonu ile döngüye al ve her bir öğe için bir Card bileşeni oluştur */}
                    {features.map((feature, index) => (
                        <Col key={index} span={8}>
                            <Card 
                                hoverable
                                cover={<img alt={feature.title} src={feature.image} />}
                            >
                                <Meta title={feature.title} description={feature.description} />
                            </Card>
                        </Col>
                    ))}
                </Row>
            </div>
        </div>
    );
}

export default AppFeature;
