import React from 'react';
import { ArrowRightCircle } from 'react-bootstrap-icons';
import AppHero from '../components/home/hero';
import AppAbout from '../components/home/about';
import AppFeature from '../components/home/feature';
import { Banner } from '../components/home/banner';
import AppHeader from '../components/common/header';
import AppWorks from '../components/home/works';
const items = [
  {
      key: '1',
      title: 'Trakya Üniversitesi Bilgisayar Mühendisliği 3. sınıf öğrencisiyim.',
      content: 'Derslerime gereken önemi göstererek 1. ve 2. sınıftaki ortalamalarımla sınıf birinciliğini elde ettim.',
      icon: <i className="fas fa-chart-pie"></i>,
  },
  {
      key: '2',
      title: 'Python, C, 8086 Assembly, Java, Flutter ve Dart programlama dillerini öğrendim.',
      content: 'Flutter kullanarak 2 adet proje geliştirdim.',
      icon: <i className="fas fa-desktop"></i>,
  },
  {
      key: '3',
      title: "Projelerim",
      content: 'İlk Projem Trakya Üniversitesindeki öğrencilerin kullanabileceği bir haberleşme uygulamasıydı ve sınıf arkadaşlarımla birlikte geliştirdik.Diğer projem ise bireysel olarak geliştirdiğim, “Design Patterns” hakkında bilgi veren bir mobil uygulama oldu.',
      icon: <i className="fas fa-database"></i>,
  },

];
function AppHome() {
  return (
    <div className="main">
      <Banner></Banner>
      <AppFeature></AppFeature>
      <AppHero itemsHeroList={items}/>
      <AppAbout itemsAboutList={items}/>
      <AppWorks></AppWorks>


    </div>
  );
}

export default AppHome;
