import { Row, Col, Flex } from "antd";
import React from "react";
import { Card } from 'antd';
import Image1 from "../../assets/images/cc.png"
import Image2 from "../../assets/images/python.svg"
import Image3 from "../../assets/images/flutterjpg.jpg"


const { Meta } = Card;

function AppFeature() {
    // Resimler ve açıklamaları bir liste içinde tanımla
    const features = [
        { image: Image1, title: "C", description: "C programlama dili" },
        { image: Image3, title: "Flutter", description: "Flutter framework" },
        { image: Image2, title: "Python", description: "Python programlama dili" },

    ];

    return (
        <div className="block featureBlock bgGray">
            <div className="container-fluid">
                <Row gutter={[16, 16]}>
                    {/* features listesini map fonksiyonu ile döngüye al ve her bir öğe için bir Card bileşeni oluştur */}
                    {features.map((feature, index) => (
                        <Col key={index} xs={{ span: 24 }} sm={{ span: 12 }} md={{ span: 8 }}>
                            <Card
                                hoverable
                                cover={<img alt={feature.title} src={feature.image} />}
                                style={{ width: '90%', height:"100%" }}
                            >
                                <div style={{ position: 'absolute', bottom: '0', width: '100%',  color: 'white', padding: '8px'}}>
                                    <Card.Meta
                                    style={{
                                        display:"flex",
                                        flexDirection:"ro"

                                    }}
                                        title={feature.title}

                                    />
                                </div>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </div>
        </div>
    );
}

export default AppFeature;
