import React from 'react';
import { Menu } from 'antd';
import { GithubOutlined, LinkedinFilled } from '@ant-design/icons';
import "./header.css"

const labels = ['Home', 'About Me', 'Skills', 'Projects', 'Blog','Contact'];
const items = labels.map((label, index) => ({
  key: index + 1,
  label: `${label}`,
}));
const socialLinks = {
  github: 'https://github.com/zehragizem',
  linkedin: "https://www.linkedin.com/in/zehra-gizem-duman-344952237/",
};


const AppHeader = () => {
  const handleSocialClick = (link) => {
    window.open(link, '_blank'); // Yeni bir sekmede bağlantıyı aç
  };

  return (
    <div className='container-fluid'>
      <div className='header'>
        <div className='logo'/>
        <Menu 
          mode='horizontal' 
          defaultSelectedKeys={['1']}
          style={{
            display: 'flex',
            justifyContent: 'flex-end',
            flex:1,
            minWidth: 0,
          }}
        >
          {items.map(item => (
            <Menu.Item key={item.key}>{item.label}</Menu.Item>
          ))}
        </Menu>
        <div style={{ display: 'flex'}}>
          {Object.keys(socialLinks).map((key, index) => (
            <div key={key} style={{ borderRadius: '50%', overflow: 'hidden', margin: '0 8px' }} onClick={() => handleSocialClick(socialLinks[key])}>
              {socialIcons[key]}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default AppHeader;
