import { Button, Carousel, Modal,ConfigProvider} from 'antd';
import React, { useState } from 'react';

const AppHero = (props) => {
  const [isModalVisible, setIsModalVisible] = useState(false);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  return (
    <ConfigProvider
      theme={{
        token: {
          defaultHoverColor:'',
          defaultHoverBorderColor:'',

          // Diğer global tokenlar buraya eklenebilir

        },

      }}
    >
    <div id="hero" className="heroBlock">
      <Carousel>
        {props.itemsHeroList.map(item => (

          <div key={item.key} className="container-fluid">
            <div className='content'>
              <h3>{item.title}</h3>
              <p>{item.content}</p>
              <div className="btnHolder">
                <Button type="primary" size="large" href={item.link} target="_blank" rel="noopener noreferrer" style={{ backgroundColor: "#a222bc" }}>Github Repository</Button>

                  <Button
                    size="large"
                    onClick={showModal}
                    className="watch-demo-button"
                  >
                    <i className="fas fa-desktop"></i> Watch a Demo
                  </Button>
                </div>


              </div>
            </div>


        ))}
      </Carousel>
      <Modal
        title="Basic Modal"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
        cancelText="Close"
        closable={false}
        okButtonProps={{ style: { display: 'none' } }}
        cancelButtonProps={{ type: 'primary' }}
      >
        <iframe title='video' width="100%" height="500" src='https://www.youtube.com/embed/DxNu1tVksqM'></iframe>
      </Modal>
    </div>
    </ConfigProvider>
  );
}

export default AppHero;
