        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

  return (
    <div className="heroBlock">

        <Carousel >
          {props.itemsHeroList.map(item => (
            <div key={item.key}>
              <div className="content">
                <h3>{item.title}</h3>
                <p>{item.content}</p>
                <div className="btnHolder">
                  <Button type="primary" size="large">Learn More</Button>

                  <Button size="large"><i className="fas fa-desktop" onClick={showModal}></i> Watch a Demo</Button>
                </div>
              </div>
            </div>
          ))}
        </Carousel>
      </div>

  );
}

export default AppHero;
