import { BackTop } from 'antd';
import { useLocation } from 'react-router-dom';

const AppFooter = () => {
    const location = useLocation();

    // Eğer mevcut sayfa "/" ise BackTop bileşenini görünmez yap
    const showBackTop = location.pathname !== '/src/components/home';

    return (
        <div className="container-fluid">
            <div className="footer">
                <div className="logo">
                    <i className="fa-solid fa-crown"></i>
                    <a href="http://google.com"> Gizem</a>
                </div>
                <ul className="socials">
                    <li><a href="https://www.facebook.com/"><i className="fab fa-facebook-f"></i></a></li>
                    <li><a href="https://www.twitter.com/"><i className="fab fa-twitter"></i></a></li>
                    <li><a href="https://www.linkedin.com/"><i className="fab fa-linkedin-in"></i></a></li>
                    <li><a href="https://www.pinterest.com/"><i className="fab fa-pinterest-p"></i></a></li>
                    <li><a href="https://www.instagram.com/"><i className="fab fa-instagram"></i></a></li>
                </ul>
                <div className="copyright">Copyright &copy; 2024 Gizem</div>
                {showBackTop && (
                    <BackTop>
                        <div className="ant-back-top-inner"><i className="fas fa-arrow-circle-up"></i></div>
                    </BackTop>
                )}
            </div>
        </div>
    );
}

export default AppFooter;
