import React from 'react';
import { Button, Carousel } from 'antd';


const AppHero= (props)=>{
  const [isModalVisible, setIsModalVisible] = useState(false);

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

  return (
    <div className="heroBlock">

        <Carousel >
          {props.itemsHeroList.map(item => (
            <div key={item.key}>
              <div className="content">
                <h3>{item.title}</h3>
                <p>{item.content}</p>
                <div className="btnHolder">
                  <Button type="primary" size="large">Learn More</Button>
                  <><Modal title="Basic Modal" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel} cancelText="Close" closable={false} okButtonProps={{style:{display:'none'}}} cancelButtonProps={{type:'primary'}}>
                        <iframe title='video' width="100%" height="500" src='https://www.youtube.com/embed/DxNu1tVksqM'></iframe>
                    </Modal></>

                </div>
              </div>
            </div>
          ))}
        </Carousel>
      </div>

  );
}

export default AppHero;
