import React from 'react';
import { ArrowRightCircle } from 'react-bootstrap-icons';
import AppHero from '../components/home/hero';
import AppAbout from '../components/home/about';
import AppFeature from '../components/home/feature';
import { Banner } from '../components/home/banner';
import App from '../components/common/header';
import AppWorks from '../components/home/works';
const items = [
  {
      key: '1',
      title: 'Design Patterns Project',
      content: 'This project showcases the implementation of various design patterns in Flutter, such as Singleton, Factory, and Observer patterns. It provides practical examples and explanations of each patterns usage in Flutter app development.',
      icon: <i className="fas fa-chart-pie"></i>,
      link: "https://github.com/zehragizem/Design_Patterns_Flutter",
  },
  {
      key: '2',
      title: 'Flutter Login Project',
      content: 'The Flutter Login Project is a comprehensive demonstration of user authentication and registration processes in Flutter applications. It covers features like user input validation, password encryption, and session management. The project provides a secure and user-friendly login and registration experience for Flutter developers.',
      icon: <i className="fas fa-desktop"></i>,
      link: "https://github.com/zehragizem/LoginOrRegister",
  },
  {
      key: '3',
      title: "C Back End Project",
      content: 'The C Back End Project focuses on developing the backend infrastructure of web applications using the C programming language. It includes the implementation of RESTful APIs, database integration, and server-side logic in C. This project offers a low-level approach to building robust and efficient backend systems for web development.',
      icon: <i className="fas fa-database"></i>,
      link: "https://github.com/zehragizem/C-PROJECT"
  },
];

function AppHome() {
  return (
    <div className="main">
      <Banner></Banner>
      <AppFeature></AppFeature>
      <AppHero itemsHeroList={items}/>
      <AppAbout itemsAboutList={items}/>
      <AppWorks></AppWorks>
      <AppFooter></AppFooter>


    </div>
  );
}

export default AppHome;
