import { Form, Input, Button, Checkbox } from 'antd';
import TextArea from 'antd/es/input/TextArea';
import React from 'react';

function AppContact() {
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form submitted');
        // İşlemlerinizi burada gerçekleştirin
    };

    // Her bir Form.Item için özelleştirilmiş kolon genişlikleri
    const layout = {
        labelCol: { span: 6 }, // Etiketlerin genişliği
        wrapperCol: { span: 18 }, // Input alanlarının genişliği
    };

    return (
        <div className="contactBlock">

                <div className="titleHolder">
                    <h2>Get In Touch</h2>
                    <p>Feel free to email us anytime for collaborations and communication</p>
                </div>
                <Form {...layout} onSubmit={handleSubmit} style={{color:""}}>
                    <Form.Item
                        label="Full Name"
                        name="fullName"
                        rules={[
                            {
                                required: true,
                                message: 'Please input your full name!',
                            },
                        ]}
                    >
                        <Input placeholder="Full Name" />
                    </Form.Item>
                    <Form.Item
                        label="Email Address"
                        name="email"
                        rules={[
                            {
                                required: true,
                                message: 'Please input your email address!',
                            },
                            {
                                type: 'email',
                                message: 'Please enter a valid email address!',
                            },
                        ]}
                    >
                        <Input placeholder="Email Address" />
                    </Form.Item>
                    <Form.Item
                        label="Telephone"
                        name="telephone"
                    >
                        <Input placeholder="Telephone" />
                    </Form.Item>
                    <Form.Item
                        label="Subject"
                        name="subject"
                    >
                        <Input placeholder="Subject" />
                    </Form.Item>
                    <Form.Item
                        label="Message"
                        name="message"
                    >
                        <TextArea placeholder="Message" />
                    </Form.Item>
                    <Form.Item
                        name="agreement"
                        valuePropName="checked"
                        rules={[
                            {
                                validator: (_, value) =>
                                    value ? Promise.resolve() : Promise.reject('Should accept agreement'),
                            },
                        ]}
                        wrapperCol={{ offset: 6, span: 18 }} // Checkbokun genişliği
                    >
                        <Checkbox>I agree with terms and conditions.</Checkbox>
                    </Form.Item>
                    <Form.Item wrapperCol={{ offset: 6, span: 18 }}>
                        <Button type="primary" htmlType="submit" className="login-form-button">
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
            </div>

    );
}

export default AppContact;
