import { useState } from 'react';
import { Card, Row, Col } from 'antd';
import Image1 from "../../assets/images/cc.png";
import Image2 from "../../assets/images/python.svg";
import Image3 from "../../assets/images/flutterjpg.jpg";

const { Meta } = Card;

function AppFeature() {
    const [isRotated, setIsRotated] = useState(false); // Dönme durumu için state tanımla

    const features = [
        { image: Image1, title: "C", description: "C programlama dili" },
        { image: Image3, title: "Flutter", description: "Flutter framework" },
        { image: Image2, title: "Python", description: "Python programlama dili" },
    ];

    const handleCardClick = () => {
        setIsRotated(!isRotated); // Dönme durumunu tersine çevir
    };

    return (
        <div className="block featureBlock bgGray">
            <div className="container-fluid">
                <Row gutter={[16, 16]}>
                    {features.map((feature, index) => (
                        <Col key={index} xs={{ span: 24 }} sm={{ span: 12 }} md={{ span: 8 }}>
                            <Card
                                hoverable
                                bordered
                                cover={<img alt={feature.title} src={feature.image} style={{ width: '100%', height: '200px', objectFit: 'cover', transform: isRotated ? 'rotateY(180deg)' : 'rotateY(0deg)' }} />}
                                style={{ width: '100%', height: '100%', transition: 'transform 0.5s' }}
                                onClick={handleCardClick} // Kartın üstüne tıklandığında dönme fonksiyonunu çağır
                            >
                                <div style={{ position: 'absolute', bottom: '0', width: '100%', color: 'white', padding: '8px' }}>
                                    <Meta
                                        title={feature.title}
                                    />
                                </div>
                            </Card>
                        </Col>
                    ))}
                </Row>
            </div>
        </div>
    );
}

export default AppFeature;
